import { useEffect, useState } from "react";
import api from "../api/client";
import Navbar from "../components/Navbar";
import Card from "../components/Card";
import Badge from "../components/Badge";

export default function Dashboard() {
  const [user, setUser] = useState({});
  const [blobs, setBlobs] = useState([]);

  useEffect(() => {
    api.get("/whoami").then((r) => setUser(r.data));
    api.get("/list_blobs").then((r) => setBlobs(r.data.blobs || []));
  }, []);

  const logout = () => {
    localStorage.removeItem("jwt");
    window.location.reload();
  };

  return (
    <>
      <Navbar user={user} onLogout={logout} />

      <div className="p-6 grid grid-cols-1 md:grid-cols-2 gap-6">
        <Card title="System Status">
          <Badge color="green">Encrypted</Badge>{" "}
          <Badge color="blue">RBAC Enabled</Badge>{" "}
          <Badge color="green">Audit Logging</Badge>
        </Card>

        <Card title="Encrypted Blobs">
          <ul className="space-y-2 text-sm">
            {blobs.map((b) => (
              <li key={b} className="flex justify-between">
                <span>{b}</span>
                <Badge color="gray">AES-GCM</Badge>
              </li>
            ))}
          </ul>
        </Card>
      </div>
    </>
  );
}
